import React from 'react';

const SetValidation = (cards, count) =>{
    if(count < 3) return false
    if(count === 3){
        var shape;
        var isValid = false;
        cards.forEach(card => {
            !shape&&(shape=card.shape);
            if(shape!==card.shape){ 
                return false;
            }
        });
    }
    return true;
}

export default SetValidation;
