import React from 'react';

const SetValidation = (cards, count) =>{


    if(count < 3) return false;
    if(count >= 3){
        let shape = null;
        var isValid = cards.map(card => {
            !shape&&(shape=card.shape);
            if(shape===card.shape){ 
                return false;
            }
        });
        return ;
    }
}

export default SetValidation;
