import React from 'react';


const SetValidation = (cards, count) =>{

    if(count < 3) return false;
    if(count >= 3){
        const isValid = cards.reduce((card, prevCard) => {
            console.log("card: ",card, "prev card: ", prevCard)
            return card.shape===prevCard.shape
        });
        console.log("isvalid: ", isValid)
    }
}

export default SetValidation;
