import React from 'react';


const SetValidation = (cards, count) =>{

    if(count < 3) return false;
    if(count >= 3){
        var isValid = cards.filter((card, index, self) => {
            return self.shape.indexOf(card.shape) === index;
        });
        console.log("is valid: ", isValid)
    }
}

export default SetValidation;
