import React from 'react';

const ButtonContainer = ({Cards, discardCard, finishGame, groupCards, sortCards}) =>{
    const Buttons = [
        {image: 'Discard.svg', name: 'discard', action: discardCard},
        {image: 'Finish.svg', name: 'finish', action: finishGame},
        {image: 'Group.svg', name: 'group', action: groupCards},
        {image: 'Sort.svg', name: 'sort', action: sortCards}
    ]
    return <div className="ButtonContainer">
        <div className="scoreTimer">

        </div>
        <div className="ButtonList">
            <img src={`./Assets/Buttons/Discard.svg`} onClick={discardCard} className="BtnImages" alt="discard" />
            <img src={`./Assets/Buttons/Finish.svg`} onClick={finishGame} className="BtnImages" alt="finish" />
            <img src={`./Assets/Buttons/Group.svg`} onClick={groupCards} className="BtnImages" alt="group" />
            <img src={`./Assets/Buttons/Sort.svg`} onClick={sortCards} className="BtnImages" alt="sort" />
        </div>
    </div>
}

export default ButtonContainer;
