import React from 'react';

var shape = [];

const SetValidation = (cards, count) =>{


    if(count < 3) return false;
    if(count >= 3){
        cards.map(card => {
            !shape&&(shape=card.shape);
            if(shape!==card.shape){ 
                console.log("valid: ", shape);
                return false;
            }
        });
    }
}

export default SetValidation;
