import React from 'react';


const SetValidation = (cards, count) =>{
    // Checks if three or more cards are present
    if(count < 3) return false;
    if(count >= 3){
        // checks if the cards are same
        const unique = [...new Set(cards.map(card => card.shape))];
        if(unique.length>1){
            return false;
        }
        const differenceAry = cards.slice(1).map(function (n, i) { return n - cards[i]; })
        const isSequence = differenceAry.every(value => value == 1)
        if(!isSequence){
           return false; 
        }
        return true;
    }
}

export default SetValidation;
